import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { mockedAuthorsList, mockedCoursesList } from '@app/shared/mocks/mocks';

@Component({
  selector: 'app-course-card',
  templateUrl: './course-card.component.html',
  styleUrls: ['./course-card.component.scss']
})
export class CourseCardComponent implements OnInit {
  @Input() editable!: boolean;
  @Output() clickOnShow = new EventEmitter()

  title!: string;
  description!: string;
  creationDate!: Date;
  duration!: number;
  authors!: string[];

  ngOnInit(): void {
  this.title = mockedCoursesList[1].title
  this.description = mockedCoursesList[1].description
  this.creationDate = new Date(mockedCoursesList[1].creationDate)
  this.duration = mockedCoursesList[1].duration
  this.authors = mockedCoursesList[1].authors.map((authorId) => {
    const author = mockedAuthorsList.find((author) => author.id === authorId);
    return author?.name as string
  })    
  }

}
<div class="course-card">
    <div class="course-card__description">
      <!-- Replace ::text:: with your code -->
      <div class="course-card__description-title">{{ title }}</div>
      <div class="course-card__description-text">{{ description }}</div>
    </div>
    <div class="course-card__info">
        <div class="course-card__info-text">
            <div>
              <b>Authors: </b>
              <ng-container *ngFor="let author of authors">
                {{author}}
              </ng-container>
            </div>
            <div><b>Duration: </b><span class="course-card__duration">{{ duration / 60 % 60 }}</span></div>
            <div><b>Created: </b><span class="course-card__date">{{ creationDate | date }}</span></div>
        </div>
        <div class="course-card__info-actions">
            <app-button [buttonText]="'Show Course'" (click)="clickOnShow.next(true)"></app-button>
            <div class="course-card__buttons" *ngIf="editable">
                <!--Enter your code here-->
                <ng-content></ng-content>
            </div>
        </div>
    </div>
</div>
