import { Component } from '@angular/core';
import { mockedCoursesList } from './shared/mocks/mocks';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {

  title = 'courses-app';
  mockedCoursesList = mockedCoursesList
  course:any = {}
  showCourseInfo: boolean = false;

  onShowCourse(courseTitle: string) {
    console.log(`Show course ${courseTitle}`)
    let selectedCourse = mockedCoursesList.find((course) => course.title === courseTitle)
    this.course = {...selectedCourse}
    this.showCourseInfo = true;
  }

  onEditCourse(courseTitle: string) {
    console.log(`Edit course ${courseTitle}`)
  }
  onDeleteCourse(courseTitle: string) {
    console.log(`Delete course ${courseTitle}`)
  }

  hideShowCourseInfo(showCourseInfo: boolean) {
  showCourseInfo
  }

}
<app-header>
  <app-button [buttonText]="'Login'"></app-button>
</app-header>
<div class="app-root">
  <!-- Search component -->
  <!--<app-search></app-search>-->

  <!-- Info component -->
  <app-info 
  [title]="'Your List Is Empty'" 
  [text]="'Please use \'Add New Course\' button to add your first course'"
  >
  <app-button [buttonText]="'add new course'"></app-button>
  </app-info>

  <!-- Course Card component -->
  <!-- <app-course-card [editable]="true">
    <div [ngStyle]="{ 'display':'flex' }">
      <app-button [iconName]="'delete'"></app-button>
      <app-button [iconName]="'edit'"></app-button>
    </div>
  </app-course-card> -->
  <ng-container *ngIf="!showCourseInfo">
     <app-courses-list 
  [editable]="true" 
  [courses]="mockedCoursesList" 
  (showCourse)="onShowCourse($event)"
  (deleteCourse)="onDeleteCourse($event)"
  (editCourse)="onEditCourse($event)"
  >
  </app-courses-list>
  </ng-container>

  <ng-container *ngIf="showCourseInfo">
    <app-course-info [course]="course" (hideCourseEmitter)="hideShowCourseInfo($event)"></app-course-info>
  </ng-container>

  <!-- Login Form component -->
  <!--<app-login-form></app-login-form>-->

  <!-- Login Form component -->
  <!--<app-registration-form></app-registration-form>-->

  <!-- Login Form component -->
  <!--<app-course-form></app-course-form>-->

  <!-- Modal component -->
  <!-- <app-modal [message]="'Message'" [title]="'Title'" [okButtonText]="'Ok'" [cancelButtonText]="'Cancel'"></app-modal> -->
</div>
